final class ParentCalendar
{
    private final String confluenceId;

    private final String spaceKey;

    private final String name;

    private final String description;

    private final DocumentReference reference;

    private final List<SubCalendar> subCalendars;

    ParentCalendar(
        String confluenceId,
        String spaceKey,
        String name,
        String description,
        DocumentReference reference,
        List<SubCalendar> subCalendars
    )
    {
        this.confluenceId = confluenceId;
        this.spaceKey = spaceKey;
        this.name = name;
        this.description = description;
        this.reference = reference;
        this.subCalendars = subCalendars;
    }

    public String confluenceId()
    {
        return confluenceId;
    }

    public String spaceKey()
    {
        return spaceKey;
    }

    public String name()
    {
        return name;
    }

    public String description()
    {
        return description;
    }

    public DocumentReference reference()
    {
        return reference;
    }

    public List<SubCalendar> subCalendars()
    {
        return subCalendars;
    }
}

final class SubCalendar
