final class RecurrentEventInfo
{
    private static final Map<WeekDay, Integer> FROM_ENUM_TO_XWIKI_DAY_NUMBER = Map.of(
        WeekDay.MON, Calendar.MONDAY,
        WeekDay.TUE, Calendar.TUESDAY,
        WeekDay.WED, Calendar.WEDNESDAY,
        WeekDay.THU, Calendar.THURSDAY,
        WeekDay.FRI, Calendar.FRIDAY,
        WeekDay.SAT, Calendar.SATURDAY,
        WeekDay.SUN, Calendar.SUNDAY
    )

    private final EventFrequency recurrentEventFrequency;

    @Nullable
    private final Date until;

    private final List<WeekDay> byDay;

    private final int nthWeekdayOfMonth

    RecurrentEventInfo(
        EventFrequency recurrentEventFrequency,
        @Nullable Date until,
        @Nullable List<WeekDay> byDay,
        int nthWeekdayOfMonth
    )
    {
        this.recurrentEventFrequency = recurrentEventFrequency;
        this.until = until;
        this.byDay = byDay;
        this.nthWeekdayOfMonth = nthWeekdayOfMonth
    }

    enum EventFrequency
    {
        YEARLY,
        MONTHLY,
        MONTHLY_SPECIFIC,
        WEEKLY,
        BIWEEKLY,
        CUSTOM_WEEKLY,
        WORK_DAYS,
        DAILY
    }

    enum WeekDay
    {
        MON,
        TUE,
        WED,
        THU,
        FRI,
        SAT,
        SUN
    }

    List<Integer> toCalendarDayNumbers()
    {
        return byDays().collect { it -> FROM_ENUM_TO_XWIKI_DAY_NUMBER.getOrDefault(it, -1) }
    }

    String toXWikiFrequency()
    {
        switch (recurrentEventFrequency()) {
            case EventFrequency.YEARLY:
                return "yearly"
            case EventFrequency.MONTHLY:
                return "monthly"
            case EventFrequency.MONTHLY_SPECIFIC:
                return "monthlySpecific"
            case EventFrequency.WEEKLY:
                return "weekly"
            case EventFrequency.BIWEEKLY:
                return "biweekly"
            case EventFrequency.WORK_DAYS:
                return "workdays"
            case EventFrequency.CUSTOM_WEEKLY:
                return "customWeekly"
            case EventFrequency.DAILY:
                return "daily"
            default:
                throw new RuntimeException("Unknown frequency type: " + recurrentEventFrequency())
        }
    }

    public EventFrequency recurrentEventFrequency()
    {
        return recurrentEventFrequency;
    }

    @Nullable
    public Date until()
    {
        return until;
    }

    @Nullable
    public List<WeekDay> byDays()
    {
        return byDay;
    }

    public int nthWeekdayOfMonth()
    {
        return nthWeekdayOfMonth
    }
}

