window.addEventListener("DOMContentLoaded", function () {
    "use strict";
    const p = document.createElement("p");
    const selectAll = document.createElement("input");
    selectAll.type = "button";
    selectAll.onclick = function () {
        for (const input of document.querySelectorAll(".confluence-export-checkbox")) {
            input.checked = true;
        }
    };
    selectAll.value = "Select All";
    selectAll.className = "btn btn-secondary";

    const selectNone = document.createElement("input");
    selectNone.type = "button";
    selectNone.onclick = function () {
        for (const input of document.querySelectorAll(".confluence-export-checkbox")) {
            input.checked = false;
        }
    };
    selectNone.value = "Select None";
    selectNone.className = "btn btn-secondary";

    const invert = document.createElement("input");
    invert.type = "button";
    invert.onclick = function () {
        for (const input of document.querySelectorAll(".confluence-export-checkbox")) {
            input.checked = !input.checked;
        }
    };
    invert.value = "Invert selection";
    invert.className = "btn btn-secondary";

    const filter = document.createElement("input");
    filter.type = "text";
    filter.placeholder = "Filterâ€¦";
    filter.ariaLabel = "Filter";
    filter.addEventListener("keyup", filterKeyUp);

    function filterKeyUp() {
        const search = filter.value.toLowerCase();
        for (const li of document.querySelectorAll("#confluence-exports > li")) {
            if (li.textContent.toLowerCase().includes(search)) {
                li.classList.remove("filtered-out");
            } else {
                li.classList.add("filtered-out");
            }
        }
    }

    p.append(selectAll, document.createTextNode(" "), selectNone, document.createTextNode(" "), document.createTextNode(" "), invert, filter);
    filterKeyUp();
    const confluenceExports = document.getElementById("confluence-exports");
    confluenceExports.parentNode.insertBefore(p, confluenceExports);

    document.getElementById("form-create").addEventListener("submit", function (e) {
        if (!document.querySelectorAll(".confluence-export-checkbox:checked")[0]) {
            alert("Please select at least one package");
            e.preventDefault();
            e.stopPropagation();
        }
    });
});

// FIXME: copy-pasted from Confluence Migrator Pro's MigrationSheet javascript extension
require(['jquery'], function ($, l10n) {
  function handleFilterPropertyChange() {
    const input = document.getElementById(
      this.classList.contains('cfm-input-filter-property')
        ? 'cfm-input-properties'
        : 'cfm-output-properties'
    );

    if (input) {
      let json = {};
      try {
        json = JSON.parse(input.value);
      } catch (e) {
        console.error("Could not parse the json value of input", input, e);
      }
      json[this.dataset.key] = this.value;
      input.value = JSON.stringify(json);
    } else {
        console.error("BUG: could not find the input to update for this property", this);
    }
  };

  $('.cfm-input-filter-property').on('change', handleFilterPropertyChange);
  $('.cfm-output-filter-property').on('change', handleFilterPropertyChange);
});
